mapper.update:
UPDATE flashim_chat_message
SET status = #{status}
WHERE message_id = #{messageId}

mapper.select:
SELECT * FROM flashim_chat_message
WHERE message_to = #{messageTo}
AND message_from = #{messageFrom}
AND status = '1'
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM flashim_chat_message
WHERE message_to = #{messageTo}
AND message_from = #{messageFrom}
AND status = '1'

SELECT * FROM flashim_chat_message WHERE message_id IN
#{id}
ORDER BY create_time ASC
